import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:3001/login', { username, password });
            const token = response.data.token;
            alert('Login successful');
            setUsername('');
            setPassword('');
            navigate('/account');
            localStorage.setItem('token', token);
        } catch (error) {
            console.log('Login Error', error);
        }
    };

    return (
        <div className='w-full h-screen flex justify-center items-center bg-gray-200'>
            <form
                className='text-center border rounded-lg w-[400px] h-[300px] p-9 bg-white shadow-lg'
                onSubmit={handleLogin}
            >
                <h2 className='text-xl font-bold mb-6'>Login</h2>
                {/* Username Input */}
                <div className='mb-4'>
                    <label className='block text-gray-700'>Username</label>
                    <input
                        className='w-full h-[40px] rounded-lg bg-gray-200 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400'
                        type='text'
                        placeholder='Username'
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </div>
                {/* Password Input */}
                <div className='mb-6'>
                    <label className='block text-gray-700'>Password</label>
                    <input
                        className='w-full h-[40px] rounded-lg bg-gray-200 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400'
                        type='password'
                        placeholder='Password'
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>
                {/* Submit Button */}
                <button
                    className='w-full h-[50px] bg-blue-500 hover:bg-blue-700 text-white font-bold rounded-lg'
                    type='submit'
                >
                    Login
                </button>
            </form>
        </div>
    );
}

export default Login;
