import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useFavorites } from '../context/FavoritesContext';
import { FaHeart, FaShoppingCart } from 'react-icons/fa';

function Navbar() {
  const isUserSignedIn = !!localStorage.getItem('token');
  const isAdmin = localStorage.getItem('userRole') === 'admin'; // Check if user is admin
  const navigate = useNavigate();
  const { cartItems } = useCart();
  const { favoriteCount } = useFavorites();

  const [darkMode, setDarkMode] = React.useState(() => {
    return localStorage.getItem('theme') === 'dark' ||
      (!('theme' in localStorage) && window.matchMedia('(prefers-color-scheme: dark)').matches);
  });

  React.useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  }, [darkMode]);

  const handleSignOut = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userRole'); // Also remove the role when signing out
    navigate('/login');
  };

  const toggleTheme = () => {
    setDarkMode(!darkMode);
  };

  const getTotalCartItems = () => {
    return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  return (
    <nav className="flex justify-around p-3 border-b border-zinc-800 items-center bg-[#1a1a1a]/90 text-zinc-300 dark:bg-gray-100 dark:text-gray-800">
      <Link to="/"><h1 className="text-3xl">TastyTracks</h1></Link>

      <div className="flex items-center gap-6">
        <ul className="flex gap-6 items-center">
          {isUserSignedIn ? (
            isAdmin ? (
              // Only show Sign Out button for admin users
              <li><button onClick={handleSignOut}>Sign Out</button></li>
            ) : (
              // Show regular user navigation items
              <>
                <li><Link to="/profile">Profile</Link></li>
                <li><Link to="/change-password">Change Password</Link></li>
                <li>
                  <Link to="/favorites" className="relative">
                    <FaHeart className="text-red-500 text-2xl" />
                    {favoriteCount > 0 && (
                      <span className="absolute -top-2 -right-3 inline-flex items-center justify-center px-2 py-1 text-xs font-bold leading-none text-red-100 bg-red-600 rounded-full">
                        {favoriteCount}
                      </span>
                    )}
                  </Link>
                </li>
                <li className="relative">
                  <Link to="/cart" className="flex items-center">
                    <FaShoppingCart className="text-white text-2xl dark:text-gray-800" />
                    {cartItems.length > 0 && (
                      <span className="absolute -top-2 -right-3 inline-flex items-center justify-center px-2 py-1 text-xs font-bold leading-none text-red-100 bg-red-600 rounded-full">
                        {getTotalCartItems()}
                      </span>
                    )}
                  </Link>
                </li>
                <li><button onClick={handleSignOut}>Sign Out</button></li>
                <li><Link to="/wishlist">Wishlist</Link></li>
                <li><Link to="/referral">Referral Program</Link></li>
                <li><Link to="/orders">Order History</Link></li>
              </>
            )
          ) : (
            // Show login/signup for non-signed-in users
            <>
              <li><Link to="/login">Login</Link></li>
              <li><Link to="/signup">Signup</Link></li>
            </>
          )}
        </ul>

        <button
          onClick={toggleTheme}
          className="p-2 rounded-full bg-gray-800 dark:bg-gray-200"
          aria-label="Toggle dark mode"
        >
          {darkMode ? (
            <span className="text-yellow-300">‚òÄÔ∏è</span>
          ) : (
            <span className="text-gray-800">üåô</span>
          )}
        </button>
      </div>
    </nav>
  );
}

export default Navbar;
