import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Cart = () => {
  const { cartItems, removeFromCart, updateQuantity, clearCart } = useCart();
  const [promoCode, setPromoCode] = useState('');
  const [discount, setDiscount] = useState(0);
  const [promoError, setPromoError] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [expiry, setExpiry] = useState('');
  const [cvv, setCvv] = useState('');
  const [cardHolder, setCardHolder] = useState('');
  const [paymentError, setPaymentError] = useState('');
  const [orderId, setOrderId] = useState(null); // Track order ID here

  if (!cartItems || cartItems.length === 0) {
    return (
      <div className="flex justify-center items-center h-64">
        <p className="text-xl text-red-500">Your cart is empty!</p>
      </div>
    );
  }

  const total = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const discountedTotal = total - (total * (discount / 100));

  const handlePromoCodeSubmit = () => {
    const validPromoCodes = {
      "SAVE10": 10,
      "SAVE20": 20,
      "FREESHIP": 5
    };

    if (validPromoCodes[promoCode]) {
      setDiscount(validPromoCodes[promoCode]);
      setPromoError('');
    } else {
      setPromoError('Invalid promo code');
      setDiscount(0);
    }
  };

  // const handlePayment = () => {
  //   if (!cardNumber || !expiry || !cvv || !cardHolder) {
  //     setPaymentError("Please fill out all payment fields.");
  //     return;
  //   }

  //   // Generate a fake order ID
  //   const generatedOrderId = Math.floor(Math.random() * 1000000);

  //   // Update order ID in state
  //   setOrderId(generatedOrderId);
  //   alert("Payment Successful! Your order has been placed.");
  //   clearCart(); // Clear cart after payment
  //   setCardNumber('');
  //   setExpiry('');
  //   setCvv('');
  //   setCardHolder('');
  //   setPaymentError('');
  // };
  // In Cart.js, modify the handlePayment function:

// Modify the handlePayment function in Cart.js

const handlePayment = async () => {
  if (!cardNumber || !expiry || !cvv || !cardHolder) {
    setPaymentError("Please fill out all payment fields.");
    return;
  }

  try {
    // Get user's token for authorization
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    // Create order items array from cart items
    const orderItems = cartItems.map(item => ({
      foodItem: item._id,
      quantity: item.quantity,
      price: item.price
    }));
    
    // Calculate total amount (with or without discount)
    const totalAmount = discount > 0 ? discountedTotal : total;

    // Assume the delivery address is the same as user's address
    // In a real app, you might want to confirm this or let them choose
    const deliveryAddress = {
      // You would get this from user profile or let them select during checkout
      houseNo: "User's House No",
      area: "User's Area",
      city: "User's City"
    };

    // Create the order
    await axios.post('http://localhost:3001/orders', {
      items: orderItems,
      totalAmount,
      deliveryAddress,
      paymentMethod: "Credit Card" // Or get this from a selection
    }, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });

    // Generate a fake order ID
    const generatedOrderId = Math.floor(Math.random() * 1000000);

    // Update order ID in state
    setOrderId(generatedOrderId);
    alert("Payment Successful! Your order has been placed.");
    clearCart(); // Clear cart after payment
    
    // Reset payment form
    setCardNumber('');
    setExpiry('');
    setCvv('');
    setCardHolder('');
    setPaymentError('');
    
    // Navigate to order history page to see the new order
    navigate('/orders');
  } catch (error) {
    console.error('Error placing order:', error);
    setPaymentError('Error processing payment. Please try again.');
  }
};
  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-4">Your Cart</h2>

      <div>
        {cartItems.map((item) => (
          <div key={item._id} className="flex justify-between items-center mb-4 bg-gray-800 p-4 rounded-lg">
            <div className="flex items-center">
              <img
                src={item.image || 'https://via.placeholder.com/150'}
                alt={item.name}
                className="w-16 h-16 object-cover rounded-md"
              />
              <div className="ml-4">
                <h3 className="text-lg font-semibold text-teal-400">{item.name}</h3>
                <p className="text-sm text-gray-300">{item.description}</p>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <button
                onClick={() => updateQuantity(item._id, 'decrement')}
                className="px-2 py-1 bg-teal-600 text-white rounded-full"
              >
                -
              </button>
              <span className="text-xl font-semibold text-white">{item.quantity}</span>
              <button
                onClick={() => updateQuantity(item._id, 'increment')}
                className="px-2 py-1 bg-teal-600 text-white rounded-full"
              >
                +
              </button>
              <span className="text-xl">${(item.price * item.quantity).toFixed(2)}</span>
              <button
                onClick={() => removeFromCart(item._id)}
                className="px-4 py-2 bg-red-600 text-white rounded-full"
              >
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-6">
        <label htmlFor="promoCode" className="text-lg font-semibold">Have a promo code?</label>
        <div className="flex items-center gap-2 mt-2">
          <input
            type="text"
            id="promoCode"
            value={promoCode}
            onChange={(e) => setPromoCode(e.target.value)}
            className="p-2 rounded-lg bg-gray-700 text-white"
            placeholder="Enter promo code"
          />
          <button
            onClick={handlePromoCodeSubmit}
            className="px-4 py-2 bg-teal-600 text-white rounded-full"
          >
            Apply
          </button>
        </div>
        {promoError && <p className="text-red-500 mt-2">{promoError}</p>}
      </div>

      <div className="flex justify-between items-center mt-4">
        <p className="text-xl font-bold">Total: ${total.toFixed(2)}</p>
        {discount > 0 && (
          <p className="text-lg text-green-500">Discount: -${(total * (discount / 100)).toFixed(2)}</p>
        )}
      </div>
      <div className="flex justify-between items-center mt-4">
        <p className="text-xl font-bold">Total After Discount: ${discountedTotal.toFixed(2)}</p>
      </div>

      {/* Payment Section */}
      <div className="mt-8 p-6 bg-gray-900 rounded-lg">
        <h2 className="text-2xl font-bold mb-4 text-white">Payment Details</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="text"
            placeholder="Card Number"
            className="p-2 rounded bg-gray-700 text-white"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
          />
          <input
            type="text"
            placeholder="Expiry Date (MM/YY)"
            className="p-2 rounded bg-gray-700 text-white"
            value={expiry}
            onChange={(e) => setExpiry(e.target.value)}
          />
          <input
            type="text"
            placeholder="CVV"
            className="p-2 rounded bg-gray-700 text-white"
            value={cvv}
            onChange={(e) => setCvv(e.target.value)}
          />
          <input
            type="text"
            placeholder="Cardholder Name"
            className="p-2 rounded bg-gray-700 text-white"
            value={cardHolder}
            onChange={(e) => setCardHolder(e.target.value)}
          />
        </div>

        {paymentError && (
          <p className="text-red-500 mt-2">{paymentError}</p>
        )}

        <button
          onClick={handlePayment}
          className="mt-4 px-6 py-2 bg-green-600 text-white rounded-full"
        >
          Place Order
        </button>
      </div>
    </div>
  );
};

export default Cart;
