import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

const Cart = () => {
  const { cartItems, removeFromCart, updateQuantity } = useCart();

  const [promoCode, setPromoCode] = useState('');
  const [discount, setDiscount] = useState(0);
  const [promoError, setPromoError] = useState('');

  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');
  const [cardHolder, setCardHolder] = useState('');
  const [paymentError, setPaymentError] = useState('');

  if (!cartItems || cartItems.length === 0) {
    return (
      <div className="flex justify-center items-center h-64">
        <p className="text-xl text-red-500">Your cart is empty!</p>
      </div>
    );
  }

  const total = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const discountedTotal = total - (total * (discount / 100));

  const handlePromoCodeSubmit = () => {
    const validPromoCodes = {
      "SAVE10": 10,
      "SAVE20": 20,
      "FREESHIP": 5
    };

    if (validPromoCodes[promoCode]) {
      setDiscount(validPromoCodes[promoCode]);
      setPromoError('');
    } else {
      setPromoError('Invalid promo code');
      setDiscount(0);
    }
  };

  const handlePlaceOrder = () => {
    if (!cardNumber || !expiryDate || !cvv || !cardHolder) {
      setPaymentError('Please fill in all payment fields.');
      return;
    }

    setPaymentError('');
    alert('Order placed successfully!');
    // You can add logic to clear cart or redirect here.
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-4 text-white">Your Cart</h2>

      <div>
        {cartItems.map((item) => (
          <div key={item._id} className="flex justify-between items-center mb-4 bg-gray-800 p-4 rounded-lg">
            <div className="flex items-center">
              <img
                src={item.image || 'https://via.placeholder.com/150'}
                alt={item.name}
                className="w-16 h-16 object-cover rounded-md"
              />
              <div className="ml-4">
                <h3 className="text-lg font-semibold text-teal-400">{item.name}</h3>
                <p className="text-sm text-gray-300">{item.description}</p>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <button
                onClick={() => updateQuantity(item._id, 'decrement')}
                className="px-2 py-1 bg-teal-600 text-white rounded-full"
              >
                -
              </button>
              <span className="text-xl font-semibold text-white">{item.quantity}</span>
              <button
                onClick={() => updateQuantity(item._id, 'increment')}
                className="px-2 py-1 bg-teal-600 text-white rounded-full"
              >
                +
              </button>
              <span className="text-xl text-white">${(item.price * item.quantity).toFixed(2)}</span>
              <button
                onClick={() => removeFromCart(item._id)}
                className="px-4 py-2 bg-red-600 text-white rounded-full"
              >
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-6">
        <label htmlFor="promoCode" className="text-lg font-semibold text-white">Have a promo code?</label>
        <div className="flex items-center gap-2 mt-2">
          <input
            type="text"
            id="promoCode"
            value={promoCode}
            onChange={(e) => setPromoCode(e.target.value)}
            className="p-2 rounded-lg bg-gray-700 text-white"
            placeholder="Enter promo code"
          />
          <button
            onClick={handlePromoCodeSubmit}
            className="px-4 py-2 bg-teal-600 text-white rounded-full"
          >
            Apply
          </button>
        </div>
        {promoError && <p className="text-red-500 mt-2">{promoError}</p>}
      </div>

      <div className="flex justify-between items-center mt-4">
        <p className="text-xl font-bold text-white">Total: ${total.toFixed(2)}</p>
        {discount > 0 && (
          <p className="text-lg text-green-500">Discount: -${(total * (discount / 100)).toFixed(2)}</p>
        )}
      </div>

      <div className="flex justify-between items-center mt-4 mb-10">
        <p className="text-xl font-bold text-white">Total After Discount: ${discountedTotal.toFixed(2)}</p>
      </div>

      {/* Payment Section */}
      <div className="p-6 bg-gray-800 rounded-lg">
        <h2 className="text-2xl font-bold text-white mb-4">Payment Details</h2>

        <div className="mb-4">
          <input
            type="text"
            placeholder="Card Number"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            className="w-full p-2 rounded-md bg-gray-700 text-white"
          />
        </div>

        <div className="flex gap-4 mb-4">
          <input
            type="text"
            placeholder="Expiry Date (MM/YY)"
            value={expiryDate}
            onChange={(e) => setExpiryDate(e.target.value)}
            className="w-1/2 p-2 rounded-md bg-gray-700 text-white"
          />
          <input
            type="text"
            placeholder="CVV"
            value={cvv}
            onChange={(e) => setCvv(e.target.value)}
            className="w-1/2 p-2 rounded-md bg-gray-700 text-white"
          />
        </div>

        <div className="mb-4">
          <input
            type="text"
            placeholder="Cardholder Name"
            value={cardHolder}
            onChange={(e) => setCardHolder(e.target.value)}
            className="w-full p-2 rounded-md bg-gray-700 text-white"
          />
        </div>

        {paymentError && <p className="text-red-500 mb-4">{paymentError}</p>}

        <button
          onClick={handlePlaceOrder}
          className="w-full py-2 bg-teal-600 text-white font-semibold rounded-md hover:bg-teal-700 transition"
        >
          Place Order
        </button>
      </div>
    </div>
  );
};

export default Cart;
