import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext'; // Import useCart if needed for cart functionality

function Home() {
  const [foodItems, setFoodItems] = useState([]);
  const [favorites, setFavorites] = useState(() => {
    // Get favorites from localStorage (or use Context if required)
    const savedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    return savedFavorites;
  });

  useEffect(() => {
    // You can replace this with fetching from an API if needed
    setFoodItems([
      { _id: 1, name: 'Pizza', category: 'Food', price: 10, image: 'https://via.placeholder.com/150' },
      { _id: 2, name: 'Burger', category: 'Food', price: 5, image: 'https://via.placeholder.com/150' },
      { _id: 3, name: 'Pasta', category: 'Food', price: 7, image: 'https://via.placeholder.com/150' },
    ]);
  }, []);

  const handleAddToFavorites = (item) => {
    setFavorites((prevFavorites) => {
      const updatedFavorites = [...prevFavorites, item];
      // Save updated favorites to localStorage
      localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
      return updatedFavorites;
    });
  };

  return (
    <div className="w-full min-h-screen bg-[#1a1a1a] dark:bg-gray-100 text-white dark:text-gray-800 p-6">
      <h1 className="text-4xl font-bold text-center text-teal-400 dark:text-teal-600 mb-6">Food Items</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {foodItems.map((item) => (
          <div key={item._id} className="bg-gray-800 dark:bg-white rounded-lg p-4">
            <img
              src={item.image || 'https://via.placeholder.com/150'}
              alt={item.name}
              className="w-24 h-24 object-cover rounded-md"
            />
            <div>
              <h3 className="text-xl font-semibold text-teal-400 dark:text-teal-600">{item.name}</h3>
              <p className="text-sm text-gray-300 dark:text-gray-600">{item.category}</p>
              <p className="text-lg text-teal-600">${item.price}</p>
            </div>

            <button
              onClick={() => handleAddToFavorites(item)}
              className="mt-4 bg-teal-600 text-white px-4 py-2 rounded-full"
            >
              Add to Favorites
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
