import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaHeart } from 'react-icons/fa';
import axios from 'axios';

function Favorites() {
  const [foodItems, setFoodItems] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const savedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavorites(savedFavorites);
    fetchFoodItems(savedFavorites);
  }, []);

  const fetchFoodItems = async (favorites) => {
    try {
      const response = await axios.get('http://localhost:3001/food-items', {
        params: { ids: favorites.join(',') }
      });
      setFoodItems(response.data);
    } catch (error) {
      console.error("Error fetching favorite food items", error);
    }
  };

  const handleRemoveFromFavorites = (itemId) => {
    const updatedFavorites = favorites.filter(id => id !== itemId);
    setFavorites(updatedFavorites);
    localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
    fetchFoodItems(updatedFavorites);
  };

  return (
    <div className="w-full min-h-screen bg-[#1a1a1a] dark:bg-gray-100 text-white dark:text-gray-800 p-6">
      <div className="max-w-6xl mx-auto">
        <div className="text-center text-3xl mb-8">Your Favorite Items</div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {foodItems.length === 0 ? (
            <div className="flex justify-center items-center h-64">
              <p className="text-xl">You have no favorite items yet.</p>
            </div>
          ) : (
            foodItems.map(item => (
              <div key={item._id} className="bg-gray-800 dark:bg-white rounded-lg p-4">
                <img
                  src={item.image || 'https://via.placeholder.com/300x200'}
                  alt={item.name}
                  className="w-full h-48 object-cover"
                />
                <div className="p-4">
                  <h3 className="text-xl font-semibold text-teal-400 dark:text-teal-600">{item.name}</h3>
                  <p className="mt-2 text-gray-300 dark:text-gray-600">{item.description}</p>
                  <span className="bg-teal-600 text-white px-2 py-1 rounded-full text-sm mt-2">${item.price}</span>
                  <div className="flex justify-between mt-4">
                    <button
                      onClick={() => handleRemoveFromFavorites(item._id)}
                      className="text-red-500 hover:text-red-600"
                    >
                      Remove from Favorites
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default Favorites;
