import React, { useState, useEffect } from 'react';

const Profile = () => {
    const [userProfile, setUserProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    throw new Error('User token not found');
                }

                // Replace 'example@example.com' with the actual user's email
                const response = await fetch('http://localhost:3001/profile/example@example.com', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const userData = await response.json();
                    setUserProfile(userData);
                } else {
                    throw new Error('Failed to fetch user profile');
                }
            } catch (error) {
                console.error('Error fetching user profile:', error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchUserProfile();
    }, []);

    if (loading) {
        return <p>Loading...</p>; // Show loading indicator while fetching data
    }

    return (
        <div className="profile-card">
            <h1 className="profile-heading">User Profile Information:</h1>
            {error ? (
                <p className="error-message">{error}</p>
            ) : userProfile ? (
                <div>
                    <div className="profile-info">
                        <strong>Username:</strong> <span>{userProfile.username}</span>
                    </div>
                    <div className="profile-info">
                        <strong>Email:</strong> <span>{userProfile.email}</span>
                    </div>
                    {/* Add more profile information as needed */}
                </div>
            ) : (
                <p className="error-message">User profile information not found.</p>
            )}
        </div>
    );
};

export default Profile;
