import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useCart } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';

function Home() {
  const [foodItems, setFoodItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [searchQuery, setSearchQuery] = useState('');
  const [priceSort, setPriceSort] = useState('');
  const [favorites, setFavorites] = useState([]);

  const { cartItems, addToCart } = useCart();
  const navigate = useNavigate();

  useEffect(() => {
    fetchFoodItems();
  }, [selectedCategory, priceSort]);

  const fetchFoodItems = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:3001/food-items', {
        params: { category: selectedCategory, priceSort: priceSort },
      });
      setFoodItems(response.data);
      setLoading(false);
    } catch (error) {
      setError('Failed to fetch food items');
      setLoading(false);
    }
  };

  const categories = ['All', 'Appetizer', 'Main Course', 'Dessert', 'Beverage'];

  const filteredItems = foodItems.filter(item => {
    const isCategoryMatch = selectedCategory === 'All' || item.category === selectedCategory;
    const isSearchMatch = item.name.toLowerCase().includes(searchQuery.toLowerCase()) || item.description.toLowerCase().includes(searchQuery.toLowerCase());
    return isCategoryMatch && isSearchMatch;
  });

  const handlePriceSort = (e) => {
    setPriceSort(e.target.value);
  };

  const handleAddToCart = (item) => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
    } else {
      addToCart(item);
    }
  };

  const handleBuyNow = (item) => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
    } else {
      addToCart(item);
      navigate('/cart');
    }
  };

  const handleToggleFavorite = (item) => {
    if (favorites.includes(item._id)) {
      setFavorites(favorites.filter(fav => fav !== item._id));
    } else {
      setFavorites([...favorites, item._id]);
    }
  };

  return (
    <div className="w-full min-h-screen bg-[#1a1a1a] dark:bg-gray-100 text-white dark:text-gray-800 p-6">
      <div className="max-w-6xl mx-auto">
        <div className="flex justify-center items-center mb-8">
          <h1 className="text-4xl font-bold text-center text-teal-400 dark:text-teal-600">TastyTracks Menu</h1>
        </div>

        <div className="flex justify-center mb-8">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search for food..."
            className="p-2 rounded-full bg-gray-800 dark:bg-gray-300 text-gray-300 dark:text-gray-800 placeholder-gray-500"
          />
        </div>

        <div className="flex justify-center mb-8">
          <div className="flex flex-wrap gap-2">
            {categories.map(category => (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                className={`px-4 py-2 rounded-full transition-colors ${selectedCategory === category
                  ? 'bg-teal-600 text-white'
                  : 'bg-gray-800 dark:bg-gray-300 text-gray-300 dark:text-gray-800 hover:bg-gray-700 dark:hover:bg-gray-400'}`}
              >
                {category}
              </button>
            ))}
          </div>
        </div>

        <div className="flex justify-center mb-8">
          <select value={priceSort} onChange={handlePriceSort} className="px-4 py-2 rounded-md bg-gray-800 text-gray-300">
            <option value="">Sort by Price</option>
            <option value="asc">Low to High</option>
            <option value="desc">High to Low</option>
          </select>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {loading ? (
            <div className="text-center">Loading...</div>
          ) : error ? (
            <div className="text-center text-red-500">{error}</div>
          ) : (
            filteredItems.map(item => (
              <div key={item._id} className="bg-gray-800 dark:bg-white rounded-lg p-4">
                <img
                  src={item.image || 'https://via.placeholder.com/300x200'}
                  alt={item.name}
                  className="w-full h-48 object-cover"
                />
                <div className="p-4">
                  <h3 className="text-xl font-semibold text-teal-400 dark:text-teal-600">{item.name}</h3>
                  <p className="mt-2 text-gray-300 dark:text-gray-600">{item.description}</p>
                  <span className="bg-teal-600 text-white px-2 py-1 rounded-full text-sm mt-2">${item.price}</span>
                  <div className="flex justify-between mt-4">
                    <button
                      onClick={() => handleAddToCart(item)}
                      className="px-4 py-2 bg-teal-600 text-white rounded-md hover:bg-teal-700"
                    >
                      Add to Cart
                    </button>
                    <button
                      onClick={() => handleBuyNow(item)}
                      className="px-4 py-2 bg-teal-600 text-white rounded-md hover:bg-teal-700"
                    >
                      Buy Now
                    </button>
                    <button
                      onClick={() => handleToggleFavorite(item)}
                      className={`text-xl ${favorites.includes(item._id) ? 'text-red-500' : 'text-gray-300'}`}
                    >
                      <FaHeart />
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
