import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    // Function to fetch user profile based on email
    const fetchUserProfile = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`http://localhost:3001/Profile?email=${email}`);
        setUser(response.data);
        setLoading(false);
      } catch (error) {
        setError('Error fetching user profile');
        setLoading(false);
      }
    };

    // Call the function when component mounts
    if (email !== '') {
      fetchUserProfile();
    }

    // Cleanup function to reset state on unmount or email change
    return () => {
      setUser(null);
      setLoading(false);
      setError('');
    };
  }, [email]); // Trigger effect when email changes

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Email validation or further processing if needed
    // For simplicity, assuming email is valid and directly used
  };

  return (
    <div>
      <h2>User Profile</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input type="email" value={email} onChange={handleEmailChange} required />
        </label>
        <button type="submit">Fetch Profile</button>
      </form>
      
      {loading && <p>Loading profile...</p>}
      {error && <p>{error}</p>}
      {user && (
        <div>
          <p>Email: {user.email}</p>
          <p>Username: {user.username}</p>
          {/* Additional profile details can be displayed here */}
        </div>
      )}
    </div>
  );
};

export default Profile;
