import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const OrderTracking = () => {
  const { orderId } = useParams();
  const [orderStatus, setOrderStatus] = useState('');
  const [error, setError] = useState('');

  // Example order status simulation
  const orderStatuses = {
    '12345': 'Shipped',
    '67890': 'Delivered',
    '11223': 'In Progress',
    '44556': 'Canceled',
  };

  useEffect(() => {
    if (orderStatuses[orderId]) {
      setOrderStatus(orderStatuses[orderId]);
      setError('');
    } else {
      setError('Invalid Order ID. Please check and try again.');
      setOrderStatus('');
    }
  }, [orderId]);

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-4">Order Tracking</h2>

      <div className="flex flex-col gap-4">
        <p className="text-lg">Your order ID: {orderId}</p>
        
        {orderStatus && (
          <div className="mt-4 p-4 bg-gray-800 text-white rounded-lg">
            <p className="text-xl font-semibold">Order Status:</p>
            <p>{orderStatus}</p>
          </div>
        )}

        {error && <p className="text-red-500 mt-2">{error}</p>}
      </div>
    </div>
  );
};

export default OrderTracking;
