import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { ThemeContext } from '../pages/ThemeContext';

function SignUp() {
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [houseNo, setHouseNo] = useState('');
    const [area, setArea] = useState('');
    const [city, setCity] = useState('');
    const [fullName, setFullName] = useState('');
    const navigate = useNavigate();
    const { theme } = useContext(ThemeContext); // dark or light

    const handleSubmit = (event) => {
        event.preventDefault();
        const address = { houseNo, area, city };
        const userData = { email, username, password, phoneNumber, fullName, address };
        axios
            .post('http://localhost:3001/register', userData)
            .then(() => {
                alert('Registration Successful');
                resetForm();
                navigate('/login');
            })
            .catch((error) => {
                console.log('Unable to register user', error);
            });
    };

    const resetForm = () => {
        setEmail('');
        setUsername('');
        setPassword('');
        setPhoneNumber('');
        setFullName('');
        setHouseNo('');
        setArea('');
        setCity('');
    };

    const isDark = theme === 'dark';
    const bgClass = isDark ? 'bg-gray-900 text-white' : 'bg-white text-gray-900';
    const formBg = isDark ? 'bg-gray-800' : 'bg-gray-100';
    const inputBg = isDark ? 'bg-gray-700 text-white' : 'bg-gray-200 text-gray-900';

    return (
        <div className={`min-h-screen flex items-center justify-center ${bgClass} transition-colors duration-300`}>
            <div className={`w-full max-w-md ${formBg} p-8 rounded-lg shadow-lg`}>
                <h2 className='text-3xl font-semibold text-center text-teal-400 mb-6'>Sign Up</h2>
                <form onSubmit={handleSubmit}>
                    {[
                        { label: 'Full Name', value: fullName, setter: setFullName, type: 'text' },
                        { label: 'Email', value: email, setter: setEmail, type: 'email' },
                        { label: 'Username', value: username, setter: setUsername, type: 'text' },
                        { label: 'Password', value: password, setter: setPassword, type: 'password' },
                        { label: 'Phone Number', value: phoneNumber, setter: setPhoneNumber, type: 'text' },
                        { label: 'House No.', value: houseNo, setter: setHouseNo, type: 'text' },
                        { label: 'Area', value: area, setter: setArea, type: 'text' },
                        { label: 'City', value: city, setter: setCity, type: 'text' },
                    ].map((field, idx) => (
                        <div className='mb-4' key={idx}>
                            <label className='block text-sm font-medium'>{field.label}</label>
                            <input
                                className={`w-full mt-1 p-2 rounded-lg focus:ring-2 focus:ring-teal-500 focus:outline-none ${inputBg}`}
                                type={field.type}
                                placeholder={field.label}
                                value={field.value}
                                onChange={(e) => field.setter(e.target.value)}
                                required
                            />
                        </div>
                    ))}
                    <div className='flex justify-center'>
                        <button className='w-full p-3 bg-teal-500 hover:bg-teal-600 text-white font-semibold rounded-lg transition duration-300'>
                            Sign Up
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default SignUp;
