import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Profile = () => {
    const [user, setUser] = useState(null);
    const [error, setError] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({
        fullName: '',
        username: '',
        email: '',
        phoneNumber: '',
        address: {
            houseNo: '',
            area: '',
            city: ''
        },
        state: ''
    });

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('No token found');
                    return;
                }

                const response = await axios.get('http://localhost:3001/profile', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.status === 200) {
                    setUser(response.data);
                    setFormData({
                        fullName: response.data.fullName,
                        username: response.data.username,
                        email: response.data.email,
                        phoneNumber: response.data.phoneNumber,
                        address: response.data.address,
                        state: response.data.state
                    });
                } else {
                    setError('Failed to fetch user data');
                }
            } catch (error) {
                console.error('Error fetching user data:', error);
                setError('Failed to fetch user data');
            }
        };

        fetchUserData();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;

        if (name.includes('.')) {
            const [parent, child] = name.split('.');
            setFormData(prev => ({
                ...prev,
                [parent]: {
                    ...prev[parent],
                    [child]: value
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleEdit = () => {
        if (!isEditing) {
            // Only show toast when entering edit mode, not when exiting
            toast.success('Profile edit mode enabled');
            setIsEditing(true);
        } else {
            // Revert if canceling edit
            setFormData({
                fullName: user.fullName,
                username: user.username,
                email: user.email,
                phoneNumber: user.phoneNumber,
                address: user.address,
                state: user.state
            });
            setIsEditing(false);
            toast.info('Edit mode canceled');
        }
    };


    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('No token found');
                return;
            }

            const response = await axios.put('http://localhost:3001/profile', formData, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.status === 200) {
                setUser(response.data);
                setIsEditing(false);
                toast.success('Profile updated successfully!');
            } else {
                setError('Failed to update user data');
            }
        } catch (error) {
            console.error('Error updating user data:', error);
            setError('Failed to update user data');
        }
    };

    return (
        <div className="min-h-screen bg-gray-900 flex items-center justify-center">
            <div className="max-w-md mx-auto p-6 bg-gray-800 border border-gray-700 rounded-lg shadow-lg">
                <h1 className="text-2xl mb-6 text-green-400">User Account Information:</h1>
                {error && <p className="text-red-500 font-bold">{error}</p>}

                {user ? (
                    <form onSubmit={handleSubmit}>
                        <div className="mb-4">
                            <label className="text-teal-400">Full Name:</label>
                            <input
                                type="text"
                                name="fullName"
                                value={formData.fullName}
                                onChange={handleChange}
                                className="text-orange-400 w-full bg-gray-700 border border-gray-600 rounded p-2"
                                disabled={!isEditing}
                            />
                        </div>
                        <div className="mb-4">
                            <label className="text-teal-400">Username:</label>
                            <input
                                type="text"
                                name="username"
                                value={formData.username}
                                onChange={handleChange}
                                className="text-orange-400 w-full bg-gray-700 border border-gray-600 rounded p-2"
                                disabled={!isEditing}
                            />
                        </div>
                        <div className="mb-4">
                            <label className="text-teal-400">Email:</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                className="text-orange-400 w-full bg-gray-700 border border-gray-600 rounded p-2"
                                disabled={!isEditing}
                            />
                        </div>
                        <div className="mb-4">
                            <label className="text-teal-400">Phone Number:</label>
                            <input
                                type="text"
                                name="phoneNumber"
                                value={formData.phoneNumber}
                                onChange={handleChange}
                                className="text-orange-400 w-full bg-gray-700 border border-gray-600 rounded p-2"
                                disabled={!isEditing}
                            />
                        </div>
                        <div className="mb-4">
                            <label className="text-teal-400">Address:</label>
                            <input
                                type="text"
                                name="address.houseNo"
                                value={formData.address.houseNo}
                                onChange={handleChange}
                                className="text-orange-400 w-full bg-gray-700 border border-gray-600 rounded p-2"
                                disabled={!isEditing}
                                placeholder="House No."
                            />
                            <input
                                type="text"
                                name="address.area"
                                value={formData.address.area}
                                onChange={handleChange}
                                className="text-orange-400 w-full bg-gray-700 border border-gray-600 rounded p-2 mt-2"
                                disabled={!isEditing}
                                placeholder="Area"
                            />
                            <input
                                type="text"
                                name="address.city"
                                value={formData.address.city}
                                onChange={handleChange}
                                className="text-orange-400 w-full bg-gray-700 border border-gray-600 rounded p-2 mt-2"
                                disabled={!isEditing}
                                placeholder="City"
                            />
                        </div>
                        <div className="mb-4">
                            <label className="text-teal-400">State:</label>
                            <input
                                type="text"
                                name="state"
                                value={formData.state}
                                onChange={handleChange}
                                className="text-orange-400 w-full bg-gray-700 border border-gray-600 rounded p-2"
                                disabled={!isEditing}
                            />
                        </div>
                        <div className="mt-6 text-center">
                            {isEditing ? (
                                <>
                                    <button
                                        type="submit"
                                        className="bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600"
                                    >
                                        Save Changes
                                    </button>
                                    <button
                                        type="button"
                                        onClick={handleEdit}
                                        className="bg-red-500 text-white py-2 px-4 rounded ml-2 hover:bg-red-600"
                                    >
                                        Cancel
                                    </button>
                                </>
                            ) : (
                                <button
                                    type="button"
                                    onClick={handleEdit}
                                    className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
                                >
                                    Edit
                                </button>
                            )}
                        </div>
                    </form>
                ) : (
                    <p className="text-red-500 font-bold">Loading user information...</p>
                )}
                <ToastContainer position="top-center" autoClose={3000} />
            </div>
        </div>
    );
};

export default Profile;
