import React from 'react';
import { Routes, Route } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Login from './pages/Login';
import SignUp from './pages/SignUp';
import Profile from './pages/Profile';
import ChangePassword from './pages/ChangePassword';
import AdminDashboard from './pages/AdminDashboard';
import Cart from './pages/Cart';
import OrderTracking from './pages/OrderTracking';
import { CartProvider, useCart } from './context/CartContext'; // ⬅️ Added useCart
import PaymentPage from './pages/PaymentPage';

function AppContent() {
  const isUserSignedIn = !!localStorage.getItem('token');
  const { clearCart } = useCart(); // ⬅️ Correct way to access clearCart

  return (
    <div className="App">
      <Navbar />
      <Routes>
        <Route path='/' element={<Home />} />
        <Route path='/login' element={<Login />} />
        <Route path='/signup' element={<SignUp />} />
        {isUserSignedIn && <Route path='/profile' element={<Profile />} />}
        {isUserSignedIn && <Route path='/change-password' element={<ChangePassword />} />}
        {isUserSignedIn && <Route path='/admin' element={<AdminDashboard />} />}
        <Route path='/cart' element={<Cart />} />
        <Route path="/payment" element={<PaymentPage clearCart={clearCart} />} />
        <Route path='/order-tracking/:orderId' element={<OrderTracking />} />
      </Routes>
    </div>
  );
}

function App() {
  return (
    <CartProvider>
      <AppContent />
    </CartProvider>
  );
}

export default App;
