import React, { useState, useEffect } from 'react';

function Favorites() {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Get favorite items from localStorage
    const savedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavorites(savedFavorites);
  }, []);

  return (
    <div className="w-full min-h-screen bg-[#1a1a1a] dark:bg-gray-100 text-white dark:text-gray-800 p-6">
      <h1 className="text-4xl font-bold text-center text-teal-400 dark:text-teal-600 mb-6">Your Favorites</h1>

      {favorites.length === 0 ? (
        <div className="flex justify-center items-center h-64">
          <p className="text-xl">You have no favorite items yet.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {favorites.map((item, index) => (
            <div key={index} className="bg-gray-800 dark:bg-white rounded-lg p-4">
              <img
                src={item.image || 'https://via.placeholder.com/150'}
                alt={item.name}
                className="w-24 h-24 object-cover rounded-md"
              />
              <div>
                <h3 className="text-xl font-semibold text-teal-400 dark:text-teal-600">{item.name}</h3>
                <p className="text-sm text-gray-300 dark:text-gray-600">{item.category}</p>
                <p className="text-lg text-teal-600">${item.price}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Favorites;
